settings.setting:

AUI().ready(
	'aui-color-picker-popover',
	function(A) {
		var AllControls = A.all("#regularlayoutsAdminLookAndFeelSettingsPanelContent .control-group");
		AllControls.setStyle('marginBottom', '5px');

		var selectObj = A.one("#[@NAMESPACE@]navigation-color");

		var selectObjText = A.one("#[@NAMESPACE@]navigation-text-color");
		selectObj.ancestor().setStyle('float', 'left');
		selectObj.ancestor().setStyle('marginRight', '10px');

		selectObj.setStyle('width', '68px');
		selectObj.setStyle('borderLeftWidth', '30px');
		selectObj.setStyle('borderLeftColor', selectObj.get('value'));
		selectObjText.setStyle('background', selectObj.get('value'));

		var colorPicker = new A.ColorPickerPopover({
	        trigger: selectObj,
	        zIndex: 99
		}).render();

		colorPicker.on('select', function(event) {
			var color = event.color;
			event.trigger.set('value', color);
			selectObj.setStyle('borderLeftColor', color);
			selectObjText.setStyle('background', color);
		});
	}
);


